import React, { Component } from "react";
import NewsDataService from "../services/news.service";
import { Link } from "react-router-dom";

export default class NewsList extends Component {
    constructor(props) {
        super(props);
        this.retrieveNews = this.retrieveNews.bind(this);
        this.refreshList = this.refreshList.bind(this);
        this.setActiveNews = this.setActiveNews.bind(this);
        this.deleteNews = this.deleteNews.bind(this);

        this.state = {
            news: [],
            currentNews: null,
            currentIndex: -1,
        };
    }

    componentDidMount() {
        this.retrieveNews();
    }

    retrieveNews() {
        NewsDataService.getAll()
            .then(response => {
                this.setState({
                    news: response.data
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    refreshList() {
        this.retrieveNews();
        this.setState({
            currentNews: null,
            currentIndex: -1
        });
    }

    setActiveNews(news, index) {
        this.setState({
            currentNews: news,
            currentIndex: index
        });
    }

    deleteNews() {
        // NewsDataService.delete(this.state.currentNews.id)
        //     .then(response => {
        //         console.log(response.data);
        //         this.props.history.push("/news")
        //     })
        //     .catch(e => {
        //         console.log(e);
        //     });
    }

    render() {
        const { news, currentNews, currentIndex } = this.state;

        return (
            <div>
                <div>
                    <h6>News >> News List</h6>
                    <br/>
                    <div>
                        {news &&
                        news.map((newsItem) => (
                            <div>
                                <div className="row">
                                    <div className="col-sm-1">{newsItem.title}</div>
                                    <div className="col-sm-9">{newsItem.brief}</div>
                                    <div className="col-sm-2">{newsItem.newsDate}</div>
                                </div>

                                <div className="col-sm-12">{newsItem.content}</div>
                                <div className="justify-content-end">
                                    <Link to={"/news/edit/" + newsItem.id} className="badge badge-warning">
                                        Edit
                                    </Link>
                                    <Link to={"/news/" + newsItem.id} className="badge badge-warning">
                                        View
                                    </Link>
                                    <input
                                        type="checkbox"
                                    />
                                </div>

                                <br/>
                                <br/>
                            </div>
                        ))}
                    </div>
                    <div>
                        <button
                            className="badge badge-danger mr-2"
                            onClick={this.deleteNews}
                        >
                            Delete
                        </button>
                    </div>
                </div>
            </div>
        );
    }
}
