import React, { Component } from "react";
import NewsDataService from "../services/news.service";
import {Link} from "react-router-dom";
import deleteNews from "./edit-news.component"

export default class ViewNews extends Component {
    constructor(props) {
        super(props);
        this.getNews = this.getNews.bind(this);
        // this.deleteNews = this.deleteNews.bind(this);
        this.getNews = this.getNews.bind(this);
        this.state = {
            currentNews: {
                id: null,
                title: "",
                newsDate: "",
                brief: "",
                content: ""
            },
            message: ""
        };
    }

    componentDidMount() {
        this.getNews(this.props.match.params.id);
    }

    getNews(id) {
        NewsDataService.get(id)
            .then(response => {
                this.setState({
                    currentNews: response.data
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    // deleteNews() {
    //     NewsDataService.delete(this.state.currentNews.id)
    //         .then(response => {
    //             console.log(response.data);
    //             this.props.history.push("/news")
    //         })
    //         .catch(e => {
    //             console.log(e);
    //         });
    // }

    render() {
        const { currentNews } = this.state;

        return (
            <div>
                <div className="edit-form">
                    <h6>News >> View News</h6>
                    <br/>
                    <form>
                        <div className="form-group">
                            <label htmlFor="title">News Title</label>
                            <input disabled
                                type="text"
                                className="form-control"
                                id="title"
                                value={currentNews.title}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="newsDate">News Date</label>
                            <input disabled
                                type="text"
                                className="form-control"
                                id="newsDate"
                                value={currentNews.newsDate}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="brief">Brief</label>
                            <textarea disabled
                                type="text"
                                className="form-control"
                                id="brief"
                                value={currentNews.brief}
                                rows="2"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="content">Content</label>
                            <textarea disabled
                                type="text"
                                className="form-control"
                                id="content"
                                value={currentNews.content}
                                rows="6"
                            />
                        </div>
                    </form>
                    <button className="btn btn-danger" onClick={deleteNews}>
                        Delete
                    </button>

                    <Link to={"/news/edit/" + currentNews.id}>
                        <button className="btn btn-warning">
                            EDIT
                        </button>
                    </Link>
                </div>

            </div>
        );
    }
}
