import React, { Component } from "react";
import NewsDataService from "../services/news.service";
import {Link} from "react-router-dom";

export default class ViewNews extends Component {
    constructor(props) {
        super(props);
        this.getNews = this.getNews.bind(this);
        this.state = {
            currentNews: {
                id: null,
                title: "",
                newsDate: "",
                brief: "",
                content: ""
            },
            message: ""
        };
    }

    componentDidMount() {
        this.getNews(this.props.match.params.id);
    }

    getNews(id) {
        NewsDataService.get(id)
            .then(response => {
                this.setState({
                    currentNews: response.data
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    render() {
        const { currentNews } = this.state;

        return (
            <div>
                {currentNews ? (
                    <div className="edit-form">
                        <h6>News >> Edit News</h6>
                        <br/>

                            <div className="form-group">
                                <label htmlFor="title">News Title</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="title"
                                    value={currentNews.title}
                                    onChange={this.onChangeTitle}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="newsDate">News Date</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="newsDate"
                                    value={currentNews.newsDate}
                                    onChange={this.onChangeNewsDate}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="brief">Brief</label>
                                <textarea
                                    type="text"
                                    className="form-control"
                                    id="brief"
                                    value={currentNews.brief}
                                    onChange={this.onChangeBrief}
                                    rows="2"
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="content">Content</label>
                                <textarea
                                    type="text"
                                    className="form-control"
                                    id="content"
                                    value={currentNews.content}
                                    onChange={this.onChangeContent}
                                    rows="6"
                                />
                            </div>

                    </div>
                ) : (
                    <div>
                        <br />
                        <p>Please click on a News...</p>
                    </div>
                )}
            </div>
        );
    }

}
