import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import { TRANSLATIONS_RU } from "./ru/translations.js";
import { TRANSLATIONS_EN } from "./en/translations.js";

const resources = {
    en: {
        translation: {
            TRANSLATIONS_EN
        }
    },
    ru: {
        translation: {
            TRANSLATIONS_RU
        }
    }
};

i18n
    .use(initReactI18next)
    .init({
        resources,
        fallbackLng: 'ru',
        keySeparator: false,
        interpolation: {
            escapeValue: false
        }
    });

export default i18n;
