import React, { Component } from "react";
import NewsDataService from "../services/news.service";
import { Link } from "react-router-dom";

export default class NewsList extends Component {
    constructor(props) {
        super(props);
        this.retrieveNews = this.retrieveNews.bind(this);
        this.refreshList = this.refreshList.bind(this);
        this.setActiveNews = this.setActiveNews.bind(this);

        this.state = {
            news: [],
            currentNews: null,
            currentIndex: -1,
        };
    }

    componentDidMount() {
        this.retrieveNews();
    }

    retrieveNews() {
        NewsDataService.getAll()
            .then(response => {
                this.setState({
                    news: response.data
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    refreshList() {
        this.retrieveNews();
        this.setState({
            currentNews: null,
            currentIndex: -1
        });
    }

    setActiveNews(news, index) {
        this.setState({
            currentNews: news,
            currentIndex: index
        });
    }

    render() {
        const { news, currentNews, currentIndex } = this.state;

        return (
            <div className="list row">
                <div className="col-md-6">
                    <h4>News List</h4>

                    <ul className="list-group">
                        {news &&
                        news.map((newsItem, index) => (
                            <li
                                className={
                                    "list-group-item " +
                                    (index === currentIndex ? "active" : "")
                                }
                                onClick={() => this.setActiveNews(newsItem, index)}
                                key={index}
                            >
                                {newsItem.title}
                            </li>
                        ))}
                    </ul>
                    <button
                        className="m-3 btn btn-sm btn-danger"
                        onClick={this.removeAllTutorials}
                    >
                        Remove All
                    </button>
                </div>
                <div className="col-md-6">
                    {currentTutorial ? (
                        <div>
                            <h4>Tutorial</h4>
                            <div>
                                <label>
                                    <strong>Title:</strong>
                                </label>{" "}
                                {currentTutorial.title}
                            </div>
                            <div>
                                <label>
                                    <strong>Description:</strong>
                                </label>{" "}
                                {currentTutorial.description}
                            </div>
                            <div>
                                <label>
                                    <strong>Status:</strong>
                                </label>{" "}
                                {currentTutorial.published ? "Published" : "Pending"}
                            </div>

                            <Link
                                to={"/tutorials/" + currentTutorial.id}
                                className="badge badge-warning"
                            >
                                Edit
                            </Link>
                        </div>
                    ) : (
                        <div>
                            <br />
                            <p>Please click on a Tutorial...</p>
                        </div>
                    )}
                </div>
            </div>
        );
    }
}
