import './App.css';
import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Switch, Route, Link } from "react-router-dom";

import AuthService from "./services/auth.service";
import AddNews from "./components/add-news.component";
import EditNews from "./components/edit-news.component";
import ViewNews from "./components/view-news.component";
import NewsList from "./components/news-list.component";
import Login from "./components/login.component"

class App extends React.Component {
    constructor(props) {
        super(props);
        this.logOut = this.logOut.bind(this);

        this.state = {
            showAddNews: false,
            showNewsList: false,
            currentUser: undefined,
        };
    }

    componentDidMount() {
        const user = AuthService.getCurrentUser();

        if (user) {
            this.setState({
                currentUser: user,
                showNewsList: user.permission.includes("d"),
                showAddNews: user.permission,
            });
        }
    }

    logOut() {
        AuthService.logout();
    }

    render() {
        const { showAddNews, showNewsList, currentUser } = this.state;

        return (
            <div>
                <div>
                    {showNewsList}
                    {showAddNews}
                </div>
                <nav className="navbar navbar-expand navbar-dark bg-dark">
                    <a href="/news" className="navbar-brand">
                        News Management
                    </a>

                    <div className="navbar-nav mr-auto">

                        {currentUser ? (
                            <div className="navbar-nav mr-auto">
                                <li className="nav-item">
                                    <a href="/auth/login" className="nav-link" onClick={this.logOut}>
                                        Logout
                                    </a>
                                </li>
                            </div>
                        ) : (
                            <div className="navbar-nav mr-auto">
                                <li className="nav-item">
                                    <Link to={"/auth/login"} className="nav-link">
                                        Login
                                    </Link>
                                </li>
                            </div>
                        )}
                    </div>
                    <div className="navbar-nav mr-auto">
                        <li className="nav-item">
                            <Link to={"/news"} className="nav-link">
                                News List
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to={"/news/add"} className="nav-link">
                                Add News
                            </Link>
                        </li>
                    </div>
                    <div className="navbar-nav text-right">
                        <li className="nav-item">
                            <Link to={"/news"} className="nav-link">
                                English
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to={"/news/add"} className="nav-link">
                                Russian
                            </Link>
                        </li>
                    </div>
                </nav>

                <div className="container mt-3">
                    <Switch>
                        <Route exact path={["/", "/news"]} component={NewsList} />
                        <Route exact path="/news/add" component={AddNews} />
                        <Route path="/news/edit/:id" component={EditNews} />
                        <Route path="/news/:id" component={ViewNews} />
                        <Route exact path="/auth/login" component={Login} />
                    </Switch>
                </div>
            </div>
        );
    }
}

export default App;
