import React, { Component } from "react";
import NewsDataService from "../services/news.service";
import { Link } from "react-router-dom";

export default class NewsList extends Component {
    constructor(props) {
        super(props);
        this.retrieveNews = this.retrieveNews.bind(this);
        this.refreshList = this.refreshList.bind(this);
        this.setActiveNews = this.setActiveNews.bind(this);

        this.state = {
            news: [],
            currentNews: null,
            currentIndex: -1,
        };
    }

    componentDidMount() {
        this.retrieveNews();
    }

    retrieveNews() {
        NewsDataService.getAll()
            .then(response => {
                this.setState({
                    news: response.data
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    refreshList() {
        this.retrieveNews();
        this.setState({
            currentNews: null,
            currentIndex: -1
        });
    }

    setActiveNews(news, index) {
        this.setState({
            currentNews: news,
            currentIndex: index
        });
    }

    render() {
        const { news, currentNews, currentIndex } = this.state;

        return (
            <div className="list row">
                <div className="col-md-6">
                    <h6>News >> News List</h6>


                    <div>
                        {news &&
                        news.map((newsItem) => (
                            <div>
                                <span>{newsItem.title}</span>
                                <span>{newsItem.brief}</span>
                                <span>{newsItem.newsDate}</span>
                                <div>{newsItem.content}</div>
                                <Link
                                    to={"/news/" + newsItem.id}
                                    className="badge badge-warning"
                                >
                                    Edit
                                </Link>
                                <Link
                                    to={"/news/" + newsItem.id}
                                    className="badge badge-warning"
                                >
                                    View
                                </Link>
                                <br/>
                                <br/>
                            </div>
                        ))}
                    </div>

                </div>
                <div className="col-md-6">
                    {currentNews ? (
                        <div>
                            <h4>News</h4>
                            <div>
                                <label>
                                    <strong>Title:</strong>
                                </label>{" "}
                                {currentNews.title}
                            </div>
                            <div>
                                <label>
                                    <strong>Brief:</strong>
                                </label>{" "}
                                {currentNews.brief}
                            </div>
                            <div>
                                <label>
                                    <strong>Content:</strong>
                                </label>{" "}
                                {currentNews.content}
                            </div>

                            <Link
                                to={"/news/" + currentNews.id}
                                className="badge badge-warning"
                            >
                                Edit
                            </Link>
                        </div>
                    ) : (
                        <div>
                            <br />
                            <p>Please click on a News...</p>
                        </div>
                    )}
                </div>
            </div>
        );
    }
}
