import React, { Component } from "react";
import NewsDataService from "../services/news.service";
import {Link} from "react-router-dom";

export default class AddNews extends Component {
    constructor(props) {
        super(props);
        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeNewsDate = this.onChangeNewsDate.bind(this);
        this.onChangeBrief = this.onChangeBrief.bind(this);
        this.onChangeContent = this.onChangeContent.bind(this);
        this.saveNews = this.saveNews.bind(this);
        this.newNews = this.newNews.bind(this);

        this.state = {
            id: null,
            title: "",
            newsDate: "",
            brief: "",
            Content: "",

            submitted: false
        };
    }

    onChangeTitle(e) {
        this.setState({
            title: e.target.value
        });
    }

    onChangeNewsDate(e) {
        this.setState({
            newsDate: e.target.value
        })
    }

    onChangeBrief(e) {
        this.setState({
            brief: e.target.value
        });
    }

    onChangeContent(e) {
        this.setState({
            content: e.target.value
        });
    }

    saveNews() {
        var data = {
            title: this.state.title,
            newsDate: this.state.newsDate,
            brief: this.state.brief,
            content: this.state.content
        };

        NewsDataService.create(data)
            .then(response => {
                this.setState({
                    id: response.data.id,
                    title: response.data.title,
                    newsDate: response.data.newsDate,
                    brief: response.data.brief,
                    content: response.data.content,

                    submitted: true
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    newNews() {
        this.setState({
            id: null,
            title: "",
            newsDate: "",
            brief: "",
            content: "",

            submitted: false
        });
    }

    render() {
        return (
            <div className="submit-form">
                {this.state.submitted ? (
                    <div>
                        <h4>You submitted successfully!</h4>
                        <button className="btn btn-success" onClick={this.newNews}>
                            Add
                        </button>
                    </div>
                ) : (
                    <div>
                        <h6>News >> Add News</h6>
                        <br/>
                        <div className="form-group">
                            <label htmlFor="title">News Title</label>
                            <input
                                type="text"
                                className="form-control"
                                id="title"
                                required
                                value={this.state.title}
                                onChange={this.onChangeTitle}
                                name="title"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="newsDate">News Date</label>
                            <input
                                type="text"
                                className="form-control"
                                id="newsDate"
                                required
                                value={this.state.newsDate}
                                onChange={this.onChangeNewsDate}
                                name="newsDate"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="brief">Brief</label>
                            <input
                                type="text"
                                className="form-control"
                                id="brief"
                                required
                                value={this.state.brief}
                                onChange={this.onChangeBrief}
                                name="brief"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="content">Content</label>
                            <input
                                type="textarea"
                                className="form-control"
                                id="content"
                                required
                                value={this.state.content}
                                onChange={this.onChangeContent}
                                name="content"
                                rows="4" cols="50"
                            />
                        </div>

                        <button onClick={this.saveNews} className="btn btn-success">
                            SAVE
                        </button>

                        <Link to={"/news"}>
                            <button onClick={this.saveNews} className="btn btn-warning">
                                CANCEL
                            </button>
                        </Link>
                    </div>
                )}
            </div>
        );
    }
}
